async function execute(input, credentials) {
  const apiKey = credentials.DEVTO_API_KEY || '';
  if (!apiKey) return { success: false, error: 'DEVTO_API_KEY not set' };

  const headers = {
    'Content-Type': 'application/json',
    'api-key': apiKey
  };

  const { action } = input;

  if (action === 'publish' || action === 'create') {
    const body = {
      article: {
        title: input.title || 'Untitled',
        body_markdown: input.content || '',
        published: !input.draft,
        tags: (input.tags || []).slice(0, 4),
        description: input.summary || '',
        canonical_url: input.canonicalUrl || undefined
      }
    };

    const res = await fetch('https://dev.to/api/articles', {
      method: 'POST',
      headers,
      body: JSON.stringify(body)
    });

    const data = await res.json();
    if (!res.ok) return { success: false, error: data.error || `HTTP ${res.status}` };

    return {
      success: true,
      data: {
        id: data.id,
        url: data.url,
        title: data.title,
        published: data.published
      }
    };
  }

  if (action === 'list') {
    const res = await fetch(`https://dev.to/api/articles/me?per_page=${input.limit || 10}`, { headers });
    const data = await res.json();
    return { success: true, data: data.map(a => ({ id: a.id, title: a.title, url: a.url, published: a.published })) };
  }

  return { success: false, error: `Unknown action: ${action}` };
}
