async function execute(input, credentials) {
  const token = credentials.MEDIUM_TOKEN || '';
  if (!token) return { success: false, error: 'MEDIUM_TOKEN not set' };

  const headers = {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${token}`
  };

  const { action } = input;

  if (action === 'publish' || action === 'create') {
    // Get user ID first
    const meRes = await fetch('https://api.medium.com/v1/me', { headers });
    const meData = await meRes.json();
    if (!meRes.ok) return { success: false, error: meData.errors?.[0]?.message || `HTTP ${meRes.status}` };
    const userId = meData.data?.id;

    const body = {
      title: input.title || 'Untitled',
      contentFormat: 'markdown',
      content: input.content || '',
      publishStatus: input.draft ? 'draft' : 'public',
      tags: input.tags || []
    };

    const res = await fetch(`https://api.medium.com/v1/users/${userId}/posts`, {
      method: 'POST',
      headers,
      body: JSON.stringify(body)
    });

    const data = await res.json();
    if (!res.ok) return { success: false, error: data.errors?.[0]?.message || `HTTP ${res.status}` };

    return {
      success: true,
      data: {
        id: data.data?.id,
        url: data.data?.url,
        title: data.data?.title,
        publishStatus: data.data?.publishStatus
      }
    };
  }

  return { success: false, error: `Unknown action: ${action}` };
}
